// src/Header.js
import React from 'react';
import Nav from './Nav';
// REMOVE THIS LINE: import logo from 'my-app/public/Logo.svg'; // THIS IS INCORRECT SYNTAX FOR PUBLIC FOLDER

function Header() {
  return (
    <header>
      {/* Correct way to reference a file directly from the public folder */}
      <img src="/Logo.svg" alt="Little Lemon Logo" className="header-logo" />
      <Nav />
    </header>
  );
}

export default Header;
